struct Solution;

use std::collections::HashMap;
impl Solution {
    pub fn three_sum_multi(a: Vec<i32>, target: i32) -> i32 {
        let mut a = a;
        a.sort();
        let mut res:i64 = 0;
        let mut distinct_elem = vec![];
        let mut elem_counter = HashMap::new();
        a.iter().for_each(|elem|{
            if let Some(cnt) = elem_counter.get(elem) {
                elem_counter.insert(elem, cnt+1);
            } else {
                distinct_elem.push(elem);
                elem_counter.insert(elem, 1);
            }
        });
        let a = &distinct_elem;
        for i in 0..a.len() {
            // 三个元素都一样的
            {
                if a[i] * 3 == target {
                    // res += C(X, 3);
                    let i_cnt = elem_counter.get(a[i]).unwrap_or(&0);
                    if *i_cnt >= 3 {
                        res += ((*i_cnt as i64) *(*i_cnt-1 as i64) *(*i_cnt-2 as i64)) as i64 / 6;
                    }
                } else {
                    // 两个元素一样，一个不一样
                    let i_cnt = elem_counter.get(a[i]).unwrap_or(&0);
                    if *i_cnt >=2 {
                        let j_cnt = elem_counter.get(&(target - 2 *a[i])).unwrap_or(&0);
                        // C(X,2) * y_cnt
                        res += (*i_cnt *(*i_cnt-1)/2 * j_cnt) as i64;
                    }
                }
            }
            if res > 1000000007 {
                res = res % 1000000007;
            }

            // 三个元素都不一样的
            let (mut j, mut k) = (i+1,a.len()-1);
            while j < k {
                if a[i] + a[j] + a[k] == target {
                    let a_i_count = elem_counter.get(&a[i]).unwrap_or(&0);
                    let a_j_count = elem_counter.get(&a[j]).unwrap_or(&0);
                    let a_k_count = elem_counter.get(&a[k]).unwrap_or(&0);
                    res += (*a_i_count as i64)*(*a_j_count as i64)*(*a_k_count as i64);
                    if res > 1000000007 {
                        res = res % 1000000007;
                    }
                    j+=1;
                    continue;
                }
                if a[i] + a[j] + a[k] < target {
                    j+=1;
                    continue;
                }
                if a[i] + a[j] + a[k] > target {
                    k-=1;
                    continue;
                }
            }
        }
        return res as i32;
    }
}

